import React, { useState, useEffect } from 'react';
import { Heart, User, Calendar, Eye, Plus, Menu, X, LogOut } from 'lucide-react';
import './App.css';

const API_BASE_URL = 'http://localhost:5000/api';

// AuthForm component - moved outside to prevent recreation on every render
const AuthForm = ({ isLogin = true, formData, setFormData, handleLogin, loading, error, setCurrentView, setError }) => (
  <div className="auth-container">
    <div className="auth-form">
      <div className="auth-header">
        <h2>{isLogin ? 'Welcome back' : 'Create account'}</h2>
        <p>{isLogin ? 'Sign in to your account' : 'Join our community of writers'}</p>
      </div>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      <div className="form-fields">
        {!isLogin && (
          <input
            type="text"
            placeholder="Full name"
            value={formData.name}
            onChange={(e) => setFormData({...formData, name: e.target.value})}
            disabled={loading}
          />
        )}
        <input
          type="email"
          placeholder="Email address"
          value={formData.email}
          onChange={(e) => setFormData({...formData, email: e.target.value})}
          disabled={loading}
        />
        <input
          type="password"
          placeholder="Password"
          value={formData.password}
          onChange={(e) => setFormData({...formData, password: e.target.value})}
          disabled={loading}
        />
      </div>

      <button
        onClick={() => handleLogin(!isLogin)}
        disabled={loading}
        className="auth-submit-btn"
      >
        {loading ? 'Please wait...' : (isLogin ? 'Sign in' : 'Create account')}
      </button>

      <div className="auth-switch">
        <button
          onClick={() => {
            setCurrentView(isLogin ? 'signup' : 'login');
            setError('');
          }}
          disabled={loading}
        >
          {isLogin ? "Don't have an account? Sign up" : "Already have an account? Sign in"}
        </button>
      </div>
    </div>
  </div>
);

// CreatePost component - moved outside to prevent recreation on every render
const CreatePost = ({ newPost, setNewPost, handleCreatePost, loading, error, setCurrentView, setError }) => (
  <div className="create-post-container">
    <div className="create-post-form">
      <h1>Write a new post</h1>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      <div className="post-form">
        <input
          type="text"
          placeholder="Post title..."
          value={newPost.title}
          onChange={(e) => setNewPost({...newPost, title: e.target.value})}
          className="title-input"
          disabled={loading}
        />

        <div className="content-section">
          <textarea
            placeholder="Tell your story..."
            value={newPost.content}
            onChange={(e) => setNewPost({...newPost, content: e.target.value})}
            className="content-input"
            disabled={loading}
          />
        </div>

        <div className="form-actions">
          <button
            onClick={() => {
              setCurrentView('home');
              setError('');
            }}
            className="cancel-btn"
            disabled={loading}
          >
            Cancel
          </button>
          <button
            onClick={handleCreatePost}
            disabled={loading || !newPost.title || !newPost.content}
            className="publish-btn"
          >
            {loading ? 'Publishing...' : 'Publish'}
          </button>
        </div>
      </div>
    </div>
  </div>
);

const App = () => {
  const [currentView, setCurrentView] = useState('home');
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const [formData, setFormData] = useState({ email: '', password: '', name: '' });
  const [newPost, setNewPost] = useState({ title: '', content: '' });

  // Check for existing token on component mount
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    if (token && userData) {
      setUser(JSON.parse(userData));
    }
    fetchPosts();
  }, []);

  // API Functions
  const apiCall = async (endpoint, options = {}) => {
    const token = localStorage.getItem('token');
    const config = {
      headers: {
        'Content-Type': 'application/json',
        ...(token && { Authorization: `Bearer ${token}` })
      },
      ...options
    };

    try {
      const response = await fetch(`${API_BASE_URL}${endpoint}`, config);
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Something went wrong');
      }

      return data;
    } catch (error) {
      throw new Error(error.message);
    }
  };

  const fetchPosts = async () => {
    try {
      setLoading(true);
      const response = await apiCall('/posts');
      // Handle the correct API response structure
      setPosts(response.data || []);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleLogin = async (isSignup = false) => {
    if (!formData.email || !formData.password || (isSignup && !formData.name)) {
      setError('Please fill in all fields');
      return;
    }

    try {
      setLoading(true);
      setError('');

      const endpoint = isSignup ? '/auth/signup' : '/auth/login';
      const payload = isSignup 
        ? { name: formData.name, email: formData.email, password: formData.password }
        : { email: formData.email, password: formData.password };

      const data = await apiCall(endpoint, {
        method: 'POST',
        body: JSON.stringify(payload)
      });

      localStorage.setItem('token', data.token);
      localStorage.setItem('user', JSON.stringify(data.user));
      setUser(data.user);
      setCurrentView('home');
      setFormData({ email: '', password: '', name: '' });

      // Refresh posts after login to get user-specific data
      fetchPosts();
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setCurrentView('home');
    fetchPosts(); // Refresh posts to remove user-specific data
  };

  const handleCreatePost = async () => {
    if (!newPost.title || !newPost.content) {
      setError('Please fill in both title and content');
      return;
    }

    try {
      setLoading(true);
      setError('');

      const response = await apiCall('/posts', {
        method: 'POST',
        body: JSON.stringify(newPost)
      });

      // Add new post to the beginning of the posts array
      setPosts([response.data, ...posts]);
      setNewPost({ title: '', content: '' });
      setCurrentView('home');
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const toggleLike = async (postId) => {
    if (!user) {
      setError('Please login to like posts');
      return;
    }

    try {
      const response = await apiCall(`/posts/${postId}/like`, {
        method: 'PUT'
      });

      // Update the post in the posts array
      setPosts(posts.map(post => 
        post._id === postId ? response.data : post
      ));
    } catch (error) {
      setError(error.message);
    }
  };

  const incrementView = async (postId) => {
    try {
      await apiCall(`/posts/${postId}/view`, {
        method: 'PUT'
      });
    } catch (error) {
      // Silently fail for view increments
      console.error(error);
    }
  };

  const Header = () => (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div className="logo">
            <h1 onClick={() => setCurrentView('home')}>
              Minimal
            </h1>
          </div>

          <nav className="desktop-nav">
            <button 
              onClick={() => setCurrentView('home')}
              className={currentView === 'home' ? 'active' : ''}
            >
              Home
            </button>
            {user ? (
              <>
                <button 
                  onClick={() => setCurrentView('create')}
                  className={currentView === 'create' ? 'active' : ''}
                >
                  Write
                </button>
                <div className="user-info">
                  <User className="icon" />
                  <span>{user.name}</span>
                </div>
                <button onClick={handleLogout} className="logout-btn">
                  <LogOut className="icon" />
                  <span>Sign Out</span>
                </button>
              </>
            ) : (
              <>
                <button 
                  onClick={() => setCurrentView('login')}
                  className="nav-link"
                >
                  Sign In
                </button>
                <button 
                  onClick={() => setCurrentView('signup')}
                  className="signup-btn"
                >
                  Sign Up
                </button>
              </>
            )}
          </nav>

          <button 
            className="mobile-menu-btn"
            onClick={() => setShowMobileMenu(!showMobileMenu)}
          >
            {showMobileMenu ? <X /> : <Menu />}
          </button>
        </div>

        {showMobileMenu && (
          <div className="mobile-nav">
            <button 
              onClick={() => { setCurrentView('home'); setShowMobileMenu(false); }}
            >
              Home
            </button>
            {user ? (
              <>
                <button 
                  onClick={() => { setCurrentView('create'); setShowMobileMenu(false); }}
                >
                  Write
                </button>
                <div className="user-info">
                  <User className="icon" />
                  <span>{user.name}</span>
                </div>
                <button 
                  onClick={() => { handleLogout(); setShowMobileMenu(false); }}
                >
                  <LogOut className="icon" />
                  <span>Sign Out</span>
                </button>
              </>
            ) : (
              <>
                <button 
                  onClick={() => { setCurrentView('login'); setShowMobileMenu(false); }}
                >
                  Sign In
                </button>
                <button 
                  onClick={() => { setCurrentView('signup'); setShowMobileMenu(false); }}
                >
                  Sign Up
                </button>
              </>
            )}
          </div>
        )}
      </div>
    </header>
  );

  const BlogPost = ({ post }) => {
    const isLikedByUser = user && post.isLikedByUser;

    const handlePostClick = () => {
      incrementView(post._id);
    };

    return (
      <article className="blog-post" onClick={handlePostClick}>
        <div className="post-content">
          <div className="post-meta">
            <div className="author-info">
              <span className="author-name">{post.author?.name || 'Anonymous'}</span>
              <span className="separator">•</span>
              <div className="date-info">
                <Calendar className="icon" />
                <span>{new Date(post.createdAt).toLocaleDateString()}</span>
              </div>
            </div>
            <div className="post-stats">
              <div className="views">
                <Eye className="icon" />
                <span>{post.views || 0}</span>
              </div>
              <button 
                onClick={(e) => {
                  e.stopPropagation();
                  toggleLike(post._id);
                }}
                className={`like-btn ${isLikedByUser ? 'liked' : ''}`}
                disabled={!user}
              >
                <Heart className={`icon ${isLikedByUser ? 'filled' : ''}`} />
                <span>{post.likesCount || post.likes?.length || 0}</span>
              </button>
            </div>
          </div>

          <h2 className="post-title">{post.title}</h2>

          <p className="post-excerpt">
            {post.content.length > 200 ? `${post.content.substring(0, 200)}...` : post.content}
          </p>

          <div className="read-more">
            <span>Read more →</span>
          </div>
        </div>
      </article>
    );
  };





  const HomePage = () => (
    <div className="home-container">
      {/* Hero Section */}
      <div className="hero-section">
        <div className="container">
          <div className="hero-content">
            <h1>Stories worth reading</h1>
            <p>
              Discover perspectives, insights, and stories from writers who care about craft and clarity.
            </p>
            {!user && (
              <button 
                onClick={() => setCurrentView('signup')}
                className="cta-btn"
              >
                Start reading
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Error Display */}
      {error && (
        <div className="container">
          <div className="error-message">
            {error}
          </div>
        </div>
      )}

      {/* Posts */}
      <div className="posts-container">
        <div className="container">
          {loading && posts.length === 0 ? (
            <div className="loading-state">
              <div>Loading posts...</div>
            </div>
          ) : posts.length === 0 ? (
            <div className="empty-state">
              <div>No posts yet. Be the first to write something!</div>
              {user && (
                <button
                  onClick={() => setCurrentView('create')}
                  className="cta-btn"
                >
                  Write a post
                </button>
              )}
            </div>
          ) : (
            <div className="posts-grid">
              {posts.map(post => (
                <BlogPost key={post._id} post={post} />
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Floating Action Button */}
      {user && (
        <button
          onClick={() => setCurrentView('create')}
          className="fab"
        >
          <Plus />
        </button>
      )}
    </div>
  );

  return (
    <div className="app">
      <Header />

      {currentView === 'home' && <HomePage />}
      {currentView === 'login' && <AuthForm isLogin={true} />}
      {currentView === 'signup' && <AuthForm isLogin={false} />}
      {currentView === 'create' && user && <CreatePost />}
    </div>
  );
};

export default App;
